import React, { useState } from "react";

const AddStock = () => {
  const [stockLabel, setStockLabel] = useState("");
  const [stockBuyPrice, setStockBuyPrice] = useState("");
  const [stockQuantity, setStockQuantity] = useState("");
  const [stockBuyDate, setStockBuyDate] = useState("");

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      const body = { stockLabel, stockBuyPrice, stockQuantity, stockBuyDate };
      const response = fetch("http://localhost:4000/stocks", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });

      console.log(response);
    } catch (error) {
      console.error(error.message);
    }
  };

  return (
    <>
      <h1 className="text-center mt-5">Stock List</h1>
      <form className="d-flex mt-5">
        <input
          className="form-control"
          type="text"
          placeholder="Stock Label"
          value={stockLabel}
          onChange={(e) => setStockLabel(e.target.value)}
        ></input>
        <input
          className="form-control"
          type="text"
          placeholder="Price"
          value={stockBuyPrice}
          onChange={(e) => setStockBuyPrice(e.target.value)}
        ></input>
        <input
          className="form-control"
          type="text"
          placeholder="Quantity"
          value={stockQuantity}
          onChange={(e) => setStockQuantity(e.target.value)}
        ></input>
        <input
          className="form-control"
          type="text"
          placeholder="Date"
          value={stockBuyDate}
          onChange={(e) => setStockBuyDate(e.target.value)}
        ></input>
        <button className="btn btn-success" onSubmit={onSubmit}>
          add
        </button>
      </form>
    </>
  );
};

export default AddStock;
