import React from "react";
import { useEffect, useState } from "react";

const ListStocks = () => {
  const [stocks, setStocks] = useState([]);

  useEffect(() => {
    getStocks();
  }, []);

  const getStocks = async () => {
    try {
      const response = await fetch("http://localhost:4000/stocks");
      setStocks(await response.json());
    } catch (error) {
      console.error(error.message);
    }
  };

  const deleteStock = async (id) => {
    try {
      const response = await fetch(`http://localhost:4000/stocks/${id}`, {
        method: "DELETE",
      });

      console.log(response);
    } catch (error) {
      console.error(error.message);
    }
  };

  return (
    <>
      <div class="row justify-content-center">
        {stocks.map((stock) => (
          <div className="card w-25 mt-3 ml-3 mr-3" id={stock.stock_id}>
            <div className="card-body">
              <h5 className="card-title">{stock.stock_label}</h5>
              <p className="card-text">{stock.stock_buy_price}</p>
              <p className="card-text">{stock.stock_quantity}</p>
              <p className="card-text">{stock.stock_buy_date}</p>
              <button className="btn btn-primary">Edit</button>
              <button
                className="btn btn-danger"
                onClick={deleteStock(stock.stock_id)}
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default ListStocks;
