import React, { useState } from "react";

const AddStock = () => {
  const [stcoks, setStocks] = useState([]);

  const handleOnSubmit = async (e) => {
    e.preventDefault();
    try {
      const body = {
        stock_label: stockLabel,
        stock_buy_price: stockBuyPrice,
        stock_quantity: stockQuantity,
        stock_buy_date: stockBuyDate,
      };
      console.log(body);
      const response = fetch("http://localhost:4000/stocks", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });

      window.location("/");
    } catch (error) {
      console.error(error.message);
    }
  };

  return (
    <>
      <h1 className="text-center mt-5">Stock List</h1>
      <form className="d-flex mt-5" onSubmit={handleOnSubmit}>
        <input
          className="form-control"
          type="text"
          placeholder="Stock Label"
          value={stockLabel}
          onChange={(e) => setStockLabel(e.target.value)}
        ></input>
        <input
          className="form-control"
          type="text"
          placeholder="Price"
          value={stockBuyPrice}
          onChange={(e) => setStockBuyPrice(e.target.value)}
        ></input>
        <input
          className="form-control"
          type="text"
          placeholder="Quantity"
          value={stockQuantity}
          onChange={(e) => setStockQuantity(e.target.value)}
        ></input>
        <input
          className="form-control"
          type="text"
          placeholder="Date"
          value={stockBuyDate}
          onChange={(e) => setStockBuyDate(e.target.value)}
        ></input>
        <button className="btn btn-success">add</button>
      </form>
    </>
  );
};

export default AddStock;
